  /* Base - Deep Blue Grey */
  --background: 222 47% 11%;
  --foreground: 210 40% 98%;

  /* Primary - Lighter Royal Purple */
  --primary: 262 47% 65%;
  --primary-foreground: 222 47% 11%;

  /* Secondary - Deep Teal */
  --secondary: 190 60% 20%;
